import React from 'react';
import { useNavigate, Link as RouterLink, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Link,
  Paper,
  CircularProgress,
} from '@mui/material';
import { login, fetchUserProfile } from '../redux/slices/authSlice';
import axios from 'axios';
import { MenuItem, FormControl, InputLabel, Select } from '@mui/material';
const API_URL = 'http://localhost:8000/api';

const validationSchema = Yup.object({
  username: Yup.string().required('Username is required'),
  password: Yup.string().required('Password is required'),
});

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const dispatch = useDispatch();
  const { loading, error } = useSelector((state) => state.auth);

  const formik = useFormik({
    initialValues: {
      username: '',
      password: '',
    },
    validationSchema,
    onSubmit: async (values) => {
      try {
        const result = await dispatch(login(values));
        if (result.error) {
          return; // Stop if login failed
        }

        // Fetch user profile to get user type
        const profileResult = await dispatch(fetchUserProfile());
        if (profileResult.error) {
          return; // Stop if profile fetch failed
        }

        // Check if there's a redirect path in the location state
        const redirectTo = location.state?.redirectTo;
        if (redirectTo) {
          navigate(redirectTo);
          return;
        }

        // If no redirect path, navigate based on user type
        switch (profileResult.payload.user_type) {
          case 'customer':
            navigate('/customer-dashboard');
            break;
          case 'owner':
            navigate('/owner-dashboard');
            break;
          case 'delivery':
            navigate('/delivery-dashboard');
            break;
          default:
            navigate('/');
        }
      } catch (error) {
        console.error('Login error:', error);
      }
    },
  });

  const otpFormik = useFormik({
    initialValues: {
      phone_number: '',
      user_type: '',
      otp: '',
    },
    validationSchema: Yup.object({
      phone_number: Yup.string().required('Phone number is required'),
      user_type: Yup.string().required('User type is required'),
      otp: Yup.string().length(6, 'Enter 6-digit OTP'),
    }),
    onSubmit: async (values) => {
      try {
        const { data } = await axios.post(`${API_URL}/auth/login_with_otp/`, values);
        localStorage.setItem('access_token', data.access);
        localStorage.setItem('refresh_token', data.refresh);
        localStorage.setItem('login_time', Date.now().toString());
        const profileResult = await dispatch(fetchUserProfile());
        if (profileResult.error) return;
        const redirectTo = location.state?.redirectTo;
        if (redirectTo) { navigate(redirectTo); return; }
        switch (profileResult.payload.user_type) {
          case 'customer': navigate('/customer-dashboard'); break;
          case 'owner': navigate('/owner-dashboard'); break;
          case 'delivery': navigate('/delivery-dashboard'); break;
          default: navigate('/');
        }
      } catch (e) {}
    },
  });

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          mt: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper
          elevation={3}
          sx={{
            p: 4,
            width: '100%',
            borderRadius: 2,
          }}
        >
          <Typography component="h1" variant="h4" align="center" gutterBottom>Login to Home Eats</Typography>

          <Box
            component="form"
            onSubmit={formik.handleSubmit}
            sx={{ mt: 3 }}
          >
            <TextField
              fullWidth
              id="username"
              name="username"
              label="Username"
              value={formik.values.username}
              onChange={formik.handleChange}
              error={formik.touched.username && Boolean(formik.errors.username)}
              helperText={formik.touched.username && formik.errors.username}
              margin="normal"
            />

            <TextField
              fullWidth
              id="password"
              name="password"
              label="Password"
              type="password"
              value={formik.values.password}
              onChange={formik.handleChange}
              error={formik.touched.password && Boolean(formik.errors.password)}
              helperText={formik.touched.password && formik.errors.password}
              margin="normal"
            />

            {error && (
              <Typography color="error" align="center" sx={{ mt: 2 }}>
                {error.detail || 'Login failed'}
              </Typography>
            )}

            <Button
              type="submit"
              fullWidth
              variant="contained"
              size="large"
              disabled={loading}
              sx={{ mt: 3, mb: 2 }}
            >
              {loading ? <CircularProgress size={24} /> : 'Login'}
            </Button>

            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="body2">
                Don't have an account?{' '}
                <Link 
                  component={RouterLink} 
                  to="/register" 
                  state={location.state}
                >
                  Register here
                </Link>
              </Typography>
            </Box>
          </Box>

          <Typography align="center" sx={{ my: 2 }}>Or</Typography>

          <Box component="form" onSubmit={otpFormik.handleSubmit}>
            <TextField
              fullWidth
              margin="normal"
              label="Phone Number"
              name="phone_number"
              value={otpFormik.values.phone_number}
              onChange={otpFormik.handleChange}
              error={otpFormik.touched.phone_number && Boolean(otpFormik.errors.phone_number)}
              helperText={otpFormik.touched.phone_number && otpFormik.errors.phone_number}
            />
            <FormControl fullWidth margin="normal">
              <InputLabel>User Type</InputLabel>
              <Select
                name="user_type"
                value={otpFormik.values.user_type}
                label="User Type"
                onChange={otpFormik.handleChange}
                error={otpFormik.touched.user_type && Boolean(otpFormik.errors.user_type)}
              >
                <MenuItem value="customer">Customer</MenuItem>
                <MenuItem value="owner">Tiffin Owner</MenuItem>
                <MenuItem value="delivery">Delivery Boy</MenuItem>
              </Select>
            </FormControl>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <TextField
                fullWidth
                margin="normal"
                label="OTP"
                name="otp"
                value={otpFormik.values.otp}
                onChange={otpFormik.handleChange}
                error={otpFormik.touched.otp && Boolean(otpFormik.errors.otp)}
                helperText={otpFormik.touched.otp && otpFormik.errors.otp}
              />
              <Button
                sx={{ height: 56, mt: 2 }}
                variant="outlined"
                onClick={async () => {
                  try {
                    if (!otpFormik.values.phone_number || !otpFormik.values.user_type) return;
                    await axios.post(`${API_URL}/auth/request_otp/`, {
                      phone_number: otpFormik.values.phone_number,
                      user_type: otpFormik.values.user_type,
                      purpose: 'login',
                    });
                  } catch (e) {}
                }}
              >
                Get OTP
              </Button>
            </Box>
            <Button type="submit" fullWidth variant="contained" size="large" sx={{ mt: 1 }}>
              Login with OTP
            </Button>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
